"use strict";exports.timeAgo=function(o){if(!o)return"";const r=new Date,t=new Date(o),n=Math.floor((r-t)/1e3);if(n<5)return"just now";if(n<60)return`${n} seconds ago`;const e=Math.floor(n/60);if(1===e)return"a minute ago";if(e<60)return`${e} minutes ago`;const a=Math.floor(e/60);if(1===a)return"an hour ago";if(a<24)return`${a} hours ago`;const u=Math.floor(a/24);if(1===u)return"yesterday";if(u<30)return`${u} days ago`;const f=Math.floor(u/30);if(1===f)return"a month ago";if(f<12)return`${f} months ago`;const s=Math.floor(u/365);return 1===s?"a year ago":`${s} years ago`};
