function o(o){if(!o)return"";const r=new Date,t=new Date(o),n=Math.floor((r-t)/1e3);if(n<5)return"just now";if(n<60)return`${n} seconds ago`;const a=Math.floor(n/60);if(1===a)return"a minute ago";if(a<60)return`${a} minutes ago`;const e=Math.floor(a/60);if(1===e)return"an hour ago";if(e<24)return`${e} hours ago`;const f=Math.floor(e/24);if(1===f)return"yesterday";if(f<30)return`${f} days ago`;const u=Math.floor(f/30);if(1===u)return"a month ago";if(u<12)return`${u} months ago`;const s=Math.floor(f/365);return 1===s?"a year ago":`${s} years ago`}export{o as timeAgo};
